import React from 'react'

const LoadMoreBtn = ({ result, page, load, handleLoadMore }) => {
    return (
        <>
            {
                result < 3 * (page - 1) ? '' :
                    //6<3*(3-1)

                    !load && <button className="btn btn-dark mx-auto d-block"
                        onClick={handleLoadMore}>
                        Load more
                    </button>
            }

        </>
    )
}

export default LoadMoreBtn
